package week1.day1;

public class FindOddNumbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 
 for (int i= 0; i <= 10; i ++)
 {
	 if(i%2!=0)
	 {
		 System.out.println("odd number is " +i);
		 
	 }
	 
	 else
	 {
		System.out.println("even number is " +i); 
	 }
	
}
	}

}
